import React from 'react';

function SearchBar(props) {
    return (
        <nav className="navbar navbar-light bg-info d-flex justify-content-center position-fixed" onSubmit={e => e.preventDefault()}>
            <form className="form-inline position-relative" >
                <input className="form-control" name ="search" onChange= {props.handleSearch} type="search" placeholder ="Search Users" />
                <button className= "btn btn-outline my-2 my-sm-0 position-absolute" onClick ={props.clearSearch}><i className="fas fa-times"></i></button>

            </form>
        </nav>
    )
}

export default SearchBar
